// auth.js - Enhanced Debug Version
import { 
  getAuth, 
  createUserWithEmailAndPassword, 
  signInWithEmailAndPassword, 
  signOut, 
  onAuthStateChanged,
  updateProfile
} from "firebase/auth";
import { 
  getFirestore, 
  doc, 
  setDoc, 
  getDoc, 
  collection 
} from "firebase/firestore";
import { app } from './firebase';

// Initialize Firebase Auth and Firestore with detailed logging
console.log('üîß Initializing Firebase Auth and Firestore...');
console.log('üì± Firebase app:', app);

const auth = getAuth(app);
const db = getFirestore(app);

console.log('üîê Auth instance:', auth);
console.log('üóÑÔ∏è Firestore instance:', db);
console.log('üÜî Auth app name:', auth.app.name);
console.log('üÜî Firestore app name:', db.app.name);

// Sign up function with MAXIMUM debugging
export const signUpUser = async (email, password, firstName, lastName) => {
  console.log('üöÄ ========== STARTING SIGN UP PROCESS ==========');
  console.log('üìß Email:', email);
  console.log('üë§ First Name:', firstName);
  console.log('üë§ Last Name:', lastName);
  console.log('üîë Password length:', password ? password.length : 'undefined');

  try {
    // Step 1: Create user with email and password
    console.log('üìß ========== STEP 1: Creating Firebase Auth User ==========');
    const userCredential = await createUserWithEmailAndPassword(auth, email, password);
    const user = userCredential.user;
    console.log('‚úÖ User created in Auth successfully!');
    console.log('üÜî User UID:', user.uid);
    console.log('üìß User email:', user.email);
    console.log('üîê User emailVerified:', user.emailVerified);
    console.log('üë§ Full user object:', user);

    // Step 2: Update the user's display name
    console.log('üë§ ========== STEP 2: Updating Display Name ==========');
    try {
      await updateProfile(user, {
        displayName: `${firstName} ${lastName}`
      });
      console.log('‚úÖ Display name updated successfully');
    } catch (profileError) {
      console.error('‚ö†Ô∏è Display name update failed (but continuing):', profileError);
    }

    // Step 3: Test Firebase connection and configuration
    console.log('üîç ========== STEP 3: Testing Firebase Configuration ==========');
    console.log('üîß Firebase app config:', app.options);
    console.log('üóÑÔ∏è Firestore instance details:', {
      app: db.app.name,
      settings: db._settings || 'no settings'
    });

    // Step 4: Test authentication state
    console.log('üîê ========== STEP 4: Checking Auth State ==========');
    console.log('üîê Current auth user:', auth.currentUser);
    console.log('üîê Auth user UID:', auth.currentUser?.uid);
    console.log('üîê Auth user email:', auth.currentUser?.email);

    // Step 5: Prepare user data
    console.log('üìù ========== STEP 5: Preparing User Data ==========');
    const userData = {
      uid: user.uid,
      email: email.toLowerCase(),
      firstName: firstName,
      lastName: lastName,
      displayName: `${firstName} ${lastName}`,
      createdAt: new Date().toISOString(),
      lastLoginAt: new Date().toISOString(),
      preferences: {
        notifications: true,
        theme: 'light'
      },
      stats: {
        videosGenerated: 0,
        problemsSolved: 0
      }
    };

    console.log('üìÑ User data prepared:', JSON.stringify(userData, null, 2));

    // Step 6: Create document reference
    console.log('üìç ========== STEP 6: Creating Document Reference ==========');
    const userDocRef = doc(db, 'users', user.uid);
    console.log('üìç Document reference:', userDocRef);
    console.log('üìç Document path:', userDocRef.path);
    console.log('üìç Collection ID:', userDocRef.parent.id);
    console.log('üìç Document ID:', userDocRef.id);

    // Step 7: Attempt to write to Firestore
    console.log('üíæ ========== STEP 7: Writing to Firestore ==========');
    console.log('üíæ About to call setDoc...');

    try {
      await setDoc(userDocRef, userData);
      console.log('‚úÖ ========== SUCCESS: Firestore document created! ==========');
    } catch (firestoreError) {
      console.error('‚ùå ========== FIRESTORE ERROR DETAILS ==========');
      console.error('‚ùå Error name:', firestoreError.name);
      console.error('‚ùå Error code:', firestoreError.code);
      console.error('‚ùå Error message:', firestoreError.message);
      console.error('‚ùå Error stack:', firestoreError.stack);
      console.error('‚ùå Full error object:', firestoreError);

      // Check if it's a permissions error
      if (firestoreError.code === 'permission-denied') {
        console.error('üö® PERMISSION DENIED - Checking auth state...');
        console.error('üîê Current user during error:', auth.currentUser);
        console.error('üîê User UID during error:', auth.currentUser?.uid);
        console.error('üîê User email verified:', auth.currentUser?.emailVerified);
      }

      throw firestoreError; // Re-throw to be caught by outer try-catch
    }

    console.log('üéâ ========== SIGN UP COMPLETED SUCCESSFULLY ==========');
    return {
      success: true,
      user: user,
      message: 'Account created successfully!'
    };

  } catch (error) {
    console.error('üí• ========== SIGN UP ERROR OCCURRED ==========');
    console.error('üí• Error type:', typeof error);
    console.error('üí• Error name:', error.name);
    console.error('üí• Error code:', error.code);
    console.error('üí• Error message:', error.message);
    console.error('üí• Error stack:', error.stack);
    console.error('üí• Full error object:', error);

    // Additional debugging for specific error types
    if (error.code) {
      console.error('üîç Error code analysis:');
      switch (error.code) {
        case 'permission-denied':
          console.error('   üö® This is a Firestore permissions error');
          console.error('   üö® Check your Firestore security rules');
          console.error('   üö® Make sure user is authenticated');
          break;
        case 'unavailable':
          console.error('   üö® Firestore service is unavailable');
          console.error('   üö® Check your internet connection');
          break;
        case 'not-found':
          console.error('   üö® Firestore database not found');
          console.error('   üö® Check your Firebase project configuration');
          break;
        case 'auth/email-already-in-use':
          console.error('   üö® Email is already registered');
          break;
        case 'auth/weak-password':
          console.error('   üö® Password is too weak');
          break;
        default:
          console.error('   üö® Unknown error code:', error.code);
      }
    }

    return {
      success: false,
      error: error.code,
      message: getErrorMessage(error.code) + ` (Debug: ${error.code})`
    };
  }
};

// Sign in function with enhanced debugging
export const signInUser = async (email, password) => {
  console.log('üîë ========== STARTING SIGN IN PROCESS ==========');
  console.log('üìß Email:', email);

  try {
    const userCredential = await signInWithEmailAndPassword(auth, email, password);
    const user = userCredential.user;
    console.log('‚úÖ User signed in successfully!');
    console.log('üÜî User UID:', user.uid);

    // Update last login time in Firestore
    console.log('üìÖ Updating last login time...');
    try {
      const userDocRef = doc(db, 'users', user.uid);
      await setDoc(userDocRef, {
        lastLoginAt: new Date().toISOString()
      }, { merge: true });
      console.log('‚úÖ Last login time updated');
    } catch (updateError) {
      console.error('‚ö†Ô∏è Failed to update last login time:', updateError);
      // Don't fail the whole sign in for this
    }

    return {
      success: true,
      user: user,
      message: 'Signed in successfully!'
    };
  } catch (error) {
    console.error('‚ùå Sign in error:', error);
    return {
      success: false,
      error: error.code,
      message: getErrorMessage(error.code)
    };
  }
};

// Sign out function
export const signOutUser = async () => {
  console.log('üö™ Starting sign out...');
  try {
    await signOut(auth);
    console.log('‚úÖ User signed out successfully');
    return {
      success: true,
      message: 'Signed out successfully!'
    };
  } catch (error) {
    console.error('‚ùå Sign out error:', error);
    return {
      success: false,
      error: error.code,
      message: 'Error signing out. Please try again.'
    };
  }
};

// Get user data from Firestore with debugging
export const getUserData = async (uid) => {
  console.log('üìñ ========== GETTING USER DATA ==========');
  console.log('üÜî Getting user data for UID:', uid);

  try {
    const userDocRef = doc(db, 'users', uid);
    console.log('üìç Document reference:', userDocRef.path);

    const userDoc = await getDoc(userDocRef);
    console.log('üìÑ Document snapshot:', userDoc);
    console.log('üìÑ Document exists:', userDoc.exists());

    if (userDoc.exists()) {
      const userData = userDoc.data();
      console.log('‚úÖ User data found:', userData);
      return {
        success: true,
        userData: userData
      };
    } else {
      console.log('‚ùå User document does not exist');
      return {
        success: false,
        message: 'User data not found'
      };
    }
  } catch (error) {
    console.error('‚ùå Error getting user data:');
    console.error('   Error code:', error.code);
    console.error('   Error message:', error.message);
    console.error('   Full error:', error);
    return {
      success: false,
      error: error.code,
      message: 'Error retrieving user data'
    };
  }
};

// Auth state observer
export const observeAuthState = (callback) => {
  console.log('üëÅÔ∏è Setting up auth state observer...');
  return onAuthStateChanged(auth, (user) => {
    console.log('üîÑ Auth state changed:', user ? `User: ${user.uid}` : 'No user');
    callback(user);
  });
};

// Enhanced error message function
const getErrorMessage = (errorCode) => {
  console.log('üîç Getting error message for code:', errorCode);

  switch (errorCode) {
    case 'auth/email-already-in-use':
      return 'An account with this email already exists.';
    case 'auth/weak-password':
      return 'Password should be at least 6 characters long.';
    case 'auth/invalid-email':
      return 'Please enter a valid email address.';
    case 'auth/user-not-found':
      return 'No account found with this email address.';
    case 'auth/wrong-password':
      return 'Incorrect password. Please try again.';
    case 'auth/too-many-requests':
      return 'Too many failed attempts. Please try again later.';
    case 'auth/network-request-failed':
      return 'Network error. Please check your connection and try again.';
    case 'auth/missing-auth-domain':
      return 'Firebase configuration error. Check your auth domain.';
    case 'permission-denied':
      return 'Permission denied. Check your Firestore security rules.';
    case 'unavailable':
      return 'Firestore service is temporarily unavailable.';
    case 'not-found':
      return 'Firestore database not found. Check your Firebase configuration.';
    default:
      return `An error occurred: ${errorCode}. Please try again.`;
  }
};

// Export auth instance for use in components
