import React, { useState, useCallback } from 'react';
import {
 Play, Book, Clock, CheckCircle, Loader, Download, Volume2,
 Search, Home, BookOpen, HelpCircle, User, Settings, Bell,
 ChevronRight, Star, Award, TrendingUp, Users, Calendar,
 MessageCircle, Target, Zap, FileText, Video, Headphones,
 ChevronDown, ArrowLeft
} from 'lucide-react';


const KhanAcademyApp = () => {
 const [currentPage, setCurrentPage] = useState('home');
 const [searchQuery, setSearchQuery] = useState('');
 const [isGenerating, setIsGenerating] = useState(false);
 const [progress, setProgress] = useState(0);
 const [currentStep, setCurrentStep] = useState('');
 const [generatedVideo, setGeneratedVideo] = useState(null);
 const [error, setError] = useState('');
 const [showExploreDropdown, setShowExploreDropdown] = useState(false);
  const [formData, setFormData] = useState({
   topic: '',
   level: 2,
   duration: 5,
   subtitleStyle: 'modern',
   wpm: 150,
   dryRun: false
 });


 // Sample data
 const exploreCategories = [
   {
     title: "Math",
     icon: "ðŸ“Š",
     subcategories: [
       "Pre-K through grade 2 (Magi Kids)",
       "Early math review",
       "2nd grade",
       "3rd grade",
       "4th grade",
       "5th grade",
       "7th grade",
       "8th grade",
       "Algebra basics",
       "Calculus",
       "Statistics"
     ]
   },
   {
     title: "Science",
     icon: "ðŸ”¬",
     subcategories: [
       "Biology",
       "Chemistry",
       "Physics",
       "Earth and space science",
       "AP Biology",
       "AP Chemistry",
       "AP Physics 1"
     ]
   },
   {
     title: "Computing",
     icon: "ðŸ’»",
     subcategories: [
       "Intro to CS - Python",
       "Computer programming",
       "Pixar in a Box",
       "Computers and the Internet"
     ]
   },
   {
     title: "Test prep",
     icon: "ðŸ“",
     subcategories: [
       "Digital SAT",
       "LSAT",
       "Get ready for SAT Prep: Math",
       "MCAT"
     ]
   },
   {
     title: "Arts & humanities",
     icon: "ðŸŽ¨",
     subcategories: [
       "World history",
       "US history",
       "Art history",
       "Grammar"
     ]
   },
   {
     title: "Economics & finance",
