 const VideoGeneratorPage = () => {
   const levelDescriptions = {
     1: { title: "Beginner", desc: "Perfect for those new to the topic", icon: "ðŸŒ±" },
     2: { title: "Intermediate", desc: "Build on existing knowledge", icon: "ðŸŒ¿" },
     3: { title: "Advanced", desc: "Deep dive into complex concepts", icon: "ðŸŒ³" }
   };


   const subtitleStyles = {
     modern: "Clean, contemporary styling",
     clean: "Minimal, distraction-free",
     typewriter: "Classic typewriter effect"
   };


   const steps = [
     "Analyzing your request...",
     "Generating educational script...",
     "Creating visual scenes...",
     "Synthesizing audio narration...",
     "Adding subtitles...",
     "Finalizing your video..."
   ];


   const simulateVideoGeneration = async () => {
     setIsGenerating(true);
     setProgress(0);
     setError('');

     try {
       for (let i = 0; i < steps.length; i++) {
         setCurrentStep(steps[i]);

         const stepDuration = i === 2 ? 3000 : i === 3 ? 2500 : 1500;

         for (let j = 0; j <= 100; j += 5) {
           setProgress((i * 100 + j) / steps.length);
           await new Promise(resolve => setTimeout(resolve, stepDuration / 20));
         }
       }

       setGeneratedVideo({
         title: formData.topic,
         duration: `${formData.duration}:00`,
         url: "https://sample-videos.com/zip/10/mp4/SampleVideo_1280x720_1mb.mp4",
         thumbnail: "https://via.placeholder.com/640x360/1865F2/ffffff?text=Educational+Video",
         size: "12.4 MB"
       });
       setCurrentStep("Video ready!");
       setProgress(100);

     } catch (err) {
       setError("Failed to generate video. Please try again.");
     } finally {
       setIsGenerating(false);
     }
   };


   const handleSubmit = () => {
     if (!formData.topic.trim()) {
       setError("Please enter a topic for your video");
       return;
     }
     simulateVideoGeneration();
   };


  const handleInputChange = useCallback((field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    setError('');
  }, []);


   return (
     <div className="max-w-7xl mx-auto px-6 py-8">
       <BackButton />
       <div className="mb-8">
         <h1 className="text-3xl font-bold text-gray-900 mb-2">AI Educational Video Generator</h1>
         <p className="text-gray-600">Create personalized learning content powered by advanced AI</p>
       </div>


       <div className="grid lg:grid-cols-2 gap-8">
         {/* Form */}
         <div className="space-y-6">
           <div className="bg-white rounded-xl shadow-sm border p-6">
             <div className="space-y-6">
               {/* Topic Input */}
               <div>
                 <label className="block text-sm font-medium text-gray-700 mb-2">
                   What would you like to learn?
                 </label>
                 <textarea
                   key="topic-textarea"
                   value={formData.topic}
                   onChange={(e) => handleInputChange('topic', e.target.value)}
                   placeholder="e.g., Teach me what a derivative is, Explain photosynthesis, How does machine learning work?"
                   className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                   rows={3}
                   disabled={isGenerating}
                 />
               </div>


               {/* Level Selection */}
               <div>
                 <label className="block text-sm font-medium text-gray-700 mb-3">
                   Learning Level
                 </label>
                 <div className="grid grid-cols-3 gap-3">
                   {Object.entries(levelDescriptions).map(([level, info]) => (
                     <button
                       key={level}
                       onClick={() => handleInputChange('level', parseInt(level))}
                       disabled={isGenerating}
                       className={`p-3 rounded-lg border-2 text-center transition-all ${
                         formData.level === parseInt(level)
                           ? 'border-blue-500 bg-blue-50 text-blue-700'
                           : 'border-gray-200 hover:border-gray-300 text-gray-600'
                       }`}
                     >
                       <div className="text-lg mb-1">{info.icon}</div>
                       <div className="text-sm font-medium">{info.title}</div>
                       <div className="text-xs text-gray-500 mt-1">{info.desc}</div>
                     </button>
                   ))}
                 </div>
               </div>


               {/* Duration */}
               <div>
                 <label className="block text-sm font-medium text-gray-700 mb-2">
                   Video Duration
                 </label>
                 <div className="flex items-center space-x-4">
                   <Clock className="w-5 h-5 text-gray-400" />
                   <input
                     type="range"
